mapper.select:
select 

from CHANNEL_ORDER_PRE_CHECK
where ORDER_SEQ_NBR = #{orderSeqNbr,jdbcType=VARCHAR}
  and TRANS_DATE = #{transDate,jdbcType=DATE}

mapper.delete:
delete from CHANNEL_ORDER_PRE_CHECK
where ORDER_SEQ_NBR = #{orderSeqNbr,jdbcType=VARCHAR}
  and TRANS_DATE = #{transDate,jdbcType=DATE}
